mapper.select:
select *
from contact_type
where u_id = #{u_id}
  and type_id = #{type_id}

mapper.update:
update contact_type
set type_name    = #{Type.TypeName},
    type_comment = #{Type.TypeComment}
where u_id = #{Type.UId}
  and type_id = #{Type.TypeId}

mapper.delete:
delete
from contact_type
where u_id = #{u_id}
  and type_id = #{type_id}
